	public function toprod(){
		set_time_limit(900);
		$iid = 'a472800181';
		$db = $this->load->database('default', true);
		$list = $this->db->from('hotels h')->join('hotel_additions a', 'a.hotel_id=h.hotel_id', 'inner')->where(['h.inter_id' => $iid])->get()->result_array();
		$arr = $this->db->from('hotel_rooms')->where(['inter_id' => $iid])->get()->result_array();
		$rooms = [];
		foreach($arr as $v){
			$rooms[$v['hotel_id']][] = $v;
		}

		$arr = $this->db->get_where('hotel_images', ['inter_id' => $iid])->result_array();
		$images = [];
		foreach($arr as $v){
			$images[$v['hotel_id']][$v['room_id']][] = $v;
		}

		$json=file_get_contents(FD_PUBLIC.'/tmp_data/daisi_lowest.json');
		$arr=json_decode($json,true);
		$lowest=[];
		foreach($arr as $v){
			$lowest[$v['hotel_id']]=$v;
		}


		$pms_auth_arr = $this->cfg;
		unset($pms_auth_arr['inter_id']);

		$addition_params = [];
		$room_params = [];
		$image_params = [];
		$lowest_params=[];
		foreach($list as $v){
			$params = array(
				'inter_id'    => $this->inter_id,
				'name'        => $v['name'],
				'address'     => $v['address'],
				'tel'         => $v['tel'],
				'email'       => $v['email'],
				'latitude'    => $v['latitude'],
				'longitude'   => $v['longitude'],
				'intro'       => $v['intro'],
				'short_intro' => $v['short_intro'],
				'intro_img'   => $v['intro_img'],
				'fax'         => $v['fax'],
				'star'        => $v['star'],
				'province'    => $v['province'],
				'city'        => $v['city'],
			);

			$db->set($params)->insert('hotels_copy');
			$hotel_id = $db->insert_id();

			$pms_auth = json_encode($pms_auth_arr);
			if(!$addition_params){
				$addition_params[] = array(
					'hotel_id'           => 0,
					'inter_id'           => $this->inter_id,
					'pms_type'           => 'shiji',
					'pms_auth'           => $pms_auth,
					'hotel_web_id'       => '',
					'pms_room_state_way' => 4,
					'pms_member_way'     => 1,
				);
			}

			$addition_params[] = array(
				'hotel_id'           => $hotel_id,
				'inter_id'           => $this->inter_id,
				'pms_type'           => 'shiji',
				'pms_auth'           => $pms_auth,
				'hotel_web_id'       => $v['hotel_web_id'],
				'pms_room_state_way' => 4,
				'pms_member_way'     => 1,
			);

			if(!empty($rooms[$v['hotel_id']])){
				foreach($rooms[$v['hotel_id']] as $t){
					$t['hotel_id'] = $hotel_id;
					$t['inter_id'] = $this->inter_id;
					$old_room_id = $t['room_id'];
					unset($t['room_id']);
					/*$rooms = array(
						'hotel_id'    => $hotel_id,
						'inter_id'    => $this->inter_id,
						'name'        => $t['name'],
						'description' => $t['description'],
						'sub_des'     => $t['sub_des'],
						'nums'        => $t['nums'],
						'bed_num'     => $t['bed_num'],
						'sort'        => $t['sort'],
						'webser_id'   => $t['webser_id'],
						'room_img'    => $t['room_img'],
						'area'        => $t['area'],
					);*/
					$db->insert('hotel_rooms_copy', $t);
					$room_id = $db->insert_id();

					if(!empty($images[$v['hotel_id']][$old_room_id])){
						foreach($images[$v['hotel_id']][$old_room_id] as $w){
							$w['inter_id'] = $this->inter_id;
							$w['hotel_id'] = $hotel_id;
							$w['room_id'] = $room_id;
							$image_params[] = $w;
						}
					}
				}

			}

			if(!empty($images[$v['hotel_id']][0])){
				foreach($images[$v['hotel_id']][0] as $t){
					$t['hotel_id'] = $hotel_id;
					$t['inter_id'] = $this->inter_id;
					$image_params[] = $t;
				}
			}

			if(!empty($lowest[$v['hotel_id']])){
				$low=$lowest[$v['hotel_id']];
				$low['hotel_id']=$hotel_id;
				$low['inter_id']=$this->inter_id;
				$lowest_params[]=$low;
			}
		}
		if($addition_params){
			$db->insert_batch('hotel_additions_copy',$addition_params);
		}
		if($image_params){
			$db->insert_batch('hotel_images_copy',$image_params);
		}
		if($lowest_params){
			$db->insert_batch('hotel_lowest_price_copy',$lowest_params);
		}

		echo' success';

	}

	public function get_lowest(){
		set_time_limit(0);
		$model_file='hotel/pms/Shiji_hotel_model';

		$this->load->model ( 'hotel/Hotel_model' );

		$startdate=date('Ymd');
		$enddate=date('Ymd',strtotime('+1 day',time()));

		$condit = array (
			'startdate' => $startdate,
			'enddate' => $enddate,
			'openid' => '',
			'member_level' => 0
		);

		$this->load->model ( 'hotel/Member_model' );
		$member_privilege = $this->Member_model->level_privilege ( $this->inter_id );
		if (! empty ( $member_privilege )) {
			$condit ['member_privilege'] = $member_privilege;
		}

		$hotels=$this->db->get_where('hotel_additions',['inter_id'=>$this->inter_id,'hotel_id>'=>0])->result_array();
		$this->load->model($model_file,'pms');

		$lowest_params=[];

		foreach($hotels as $v){
			$hotel_id=$v['hotel_id'];

			$rooms = $this->Hotel_model->get_hotel_rooms ( $this->inter_id, $hotel_id, 1 );
			if(!$rooms)
				continue;

			echo json_encode($rooms);exit;

			$pms_state=$this->pms->get_rooms_change ( $rooms, array (
				'inter_id' => $this->inter_id,
				'hotel_id' => $hotel_id
			), $condit, $v );

			$lowest_arr=[];
			foreach($pms_state as $t){
				if(!empty($t['lowest'])){
					$lowest = str_replace(',', '', $t['lowest']);
					if($lowest > 0){
						$lowest_arr[]=$lowest;
					}
				}
			}

			if($lowest_arr){
				$lowest_params[]=[
					'inter_id'=>$this->inter_id,
					'hotel_id'=>$hotel_id,
					'lowest_price' => min($lowest_arr),
					'update_time'  => date('Y-m-d H:i:s'),
				];
			}
		}

		if($lowest_params){
			echo json_encode($lowest_params);
		}
	}

	public function merge_images(){
		$json=file_get_contents(FD_PUBLIC.'/tmp_data/daisi_image_merge.json');
		$list=json_decode($json,true);
		$params=[];
		$db=$this->load->database('default',true);
		foreach($list as $v){
			if(!$v['new_hotel_id']){
				continue;
			}
			if($v['room_id']&&!$v['new_room_id'])
				continue;
			if(!$v['new_room_id'])
				$v['new_room_id']=0;

			$v['room_id']=$v['new_room_id'];
			$v['hotel_id']=$v['new_hotel_id'];
			unset($v['new_room_id'],$v['new_hotel_id'],$v['hotel_name'],$v['room_name'],$v['id']);
			$params[]=$v;
		}
		$db->insert_batch('hotel_images_copy',$params);
		echo 'success';
	}

